import { ethers } from 'ethers';

// Official Fluent testnet configuration
const FLUENT_TESTNET_CONFIG = {
  chainId: '0x51EA', // 20994 in hex
  chainName: 'Fluent Testnet',
  nativeCurrency: {
    name: 'ETH',
    symbol: 'ETH',
    decimals: 18,
  },
  rpcUrls: ['https://rpc.testnet.fluent.xyz'],
  blockExplorerUrls: ['https://testnet.fluentscan.xyz/'],
};

class WalletManager {
  constructor() {
    this.provider = null;
    this.signer = null;
    this.account = null;
    this.isConnected = false;
    this.pendingRequests = new Set();
  }

  async checkMetaMask() {
    if (!window.ethereum) {
      throw new Error('MetaMask is not installed. Please install MetaMask to use this dApp.');
    }
    return true;
  }

  async connectWallet() {
    try {
      await this.checkMetaMask();

      // Request account access
      const accounts = await window.ethereum.request({
        method: 'eth_requestAccounts',
      });

      if (accounts.length === 0) {
        throw new Error('No accounts found. Please connect your wallet.');
      }

      this.provider = new ethers.BrowserProvider(window.ethereum);
      this.signer = await this.provider.getSigner();
      this.account = accounts[0];
      this.isConnected = true;

      // Switch to Fluent testnet
      await this.switchToFluentTestnet();

      // Listen for account changes
      window.ethereum.on('accountsChanged', (accounts) => {
        if (accounts.length === 0) {
          this.disconnect();
        } else {
          this.account = accounts[0];
          window.location.reload(); // Reload to refresh the app state
        }
      });

      // Listen for network changes
      window.ethereum.on('chainChanged', (chainId) => {
        window.location.reload(); // Reload to refresh the app state
      });

      return {
        account: this.account,
        provider: this.provider,
        signer: this.signer,
      };
    } catch (error) {
      console.error('Error connecting wallet:', error);
      throw error;
    }
  }

  async switchToFluentTestnet() {
    try {
      // First check current network
      const currentChainId = await window.ethereum.request({ method: 'eth_chainId' });
      console.log('Current chain ID:', currentChainId);
      
      // If already on Fluent testnet, return
      if (currentChainId.toLowerCase() === '0x51ea') {
        console.log('Already on Fluent testnet');
        return;
      }

      // Try to switch to existing network first
      try {
        await window.ethereum.request({
          method: 'wallet_switchEthereumChain',
          params: [{ chainId: FLUENT_TESTNET_CONFIG.chainId }],
        });
        console.log('Successfully switched to Fluent testnet');
        return;
      } catch (switchError) {
        console.log('Switch failed, need to add network:', switchError.code);
        
        // Only add network if it doesn't exist (error 4902)
        if (switchError.code === 4902) {
          await window.ethereum.request({
            method: 'wallet_addEthereumChain',
            params: [FLUENT_TESTNET_CONFIG],
          });
          console.log('Successfully added and switched to Fluent testnet');
          return;
        } else {
          throw switchError;
        }
      }
    } catch (error) {
      console.error('Failed to switch to Fluent testnet:', error);
      
      // If user is on wrong network, just continue - don't block the app
      if (error.code === 4001) {
        console.log('User rejected network switch, continuing...');
        return;
      }
      
      throw new Error('Please manually add Fluent Testnet to MetaMask:\n' +
        '• Network Name: Fluent Testnet\n' +
        '• RPC URL: https://rpc.testnet.fluent.xyz\n' +
        '• Chain ID: 20994\n' +
        '• Symbol: ETH');
    }
  }

  async checkNetwork() {
    if (!this.provider) return false;

    try {
      const network = await this.provider.getNetwork();
      const chainId = Number(network.chainId);
      console.log('Current chain ID:', chainId);
      
      // Accept multiple potential Fluent chain IDs
      const validFluentIds = [20994, 84531, 421613];
      const isValid = validFluentIds.includes(chainId);
      console.log('Is valid Fluent network:', isValid);
      
      return isValid;
    } catch (error) {
      console.error('Error checking network:', error);
      return false;
    }
  }

  async getBalance() {
    if (!this.provider || !this.account) return '0';

    try {
      const balance = await this.provider.getBalance(this.account);
      return ethers.formatEther(balance);
    } catch (error) {
      console.error('Error getting balance:', error);
      return '0';
    }
  }

  disconnect() {
    this.provider = null;
    this.signer = null;
    this.account = null;
    this.isConnected = false;
  }

  formatAddress(address) {
    if (!address) return '';
    return `${address.slice(0, 6)}...${address.slice(-4)}`;
  }

  async isConnectedAndOnCorrectNetwork() {
    if (!this.isConnected || !this.provider) return false;
    return await this.checkNetwork();
  }
}

export default new WalletManager();
